package org.firstinspires.ftc.teamcode;import com.qualcomm.robotcore.eventloop.opmode.LinearOpMode;import com.qualcomm.robotcore.hardware.Servo;import java.util.Locale;import com.qualcomm.robotcore.eventloop.opmode.Autonomous;import com.qualcomm.robotcore.eventloop.opmode.TeleOp;import com.qualcomm.robotcore.util.ElapsedTime;import com.qualcomm.robotcore.hardware.DcMotor;//import com.qualcomm.robotcore.hardware.Servo;@Autonomous(name="Coba_arm", group="Autonomous")public class Coba_arm extends LinearOpMode {    // Declare OpMode members.    private ElapsedTime runtime = new ElapsedTime();    private DcMotor M3;    private Servo servoTest;        @Override    public void runOpMode() {                M3 = hardwareMap.get(DcMotor.class,"arm");        servoTest = hardwareMap.get(Servo.class, "servoTest");       telemetry.addData("Status", "Initialized");        telemetry.update();     // Wait for the game to start (driver presses PLAY)        waitForStart();               //Naik 2 detik        M3.setPower(-0.5);        sleep(300);                // Berhenti        M3.setPower(0);        sleep(200);                //Turun 2 detik        M3.setPower(0.5);        sleep(130);                // Berhenti        M3.setPower(0);        sleep(200);                 //Turun 2 detik        servoTest.setPosition(0);        sleep(3000);                //Turun 2 detik        servoTest.setPosition(1);        sleep(3000);// run until the end of the match (driver presses STOP)        while (opModeIsActive()) {            //servoTest.setPosition(1);     }}}